#include <iostream>
using namespace std; 

// Function to find maximum in arr[] of size n 
int findMax(int arr[], int n) 
{ 
    int max = arr[0]; // Initialize maximum 

    for (int i = 1; i < n; i++) 
        if (arr[i] > max) 
            max = arr[i]; 

    return max; 
} 

int main() 
{ 
    int arr[] = {3, 4, 5, 6, 7}; 
    int n = sizeof(arr)/sizeof(arr[0]); 

    // Function calling 
    cout << "Maximum element in the array is " << findMax(arr, n); 

    return 0; 
}
