def shift_word(word):
        shifted_letter = ""
        for letter in word:
            letter_idx = ord(letter) + 2
            if letter_idx > ord('z'):
                letter_idx -= 26
            shifted_letter += chr(letter_idx)
        return shifted_letter

print(shift_word('apple'))

public class ArithmeticSequence {
    public static int nthTerm(int a, int d, int n) {
        // 'a' is first term  
        // 'd' is common difference  
        // 'n' is the nth term
        int result = a + (n - 1) * d;  
        return result; 
    }
    public static void main(String[] args){  
        // Sample usage
        int a = 3;
        int d = 2;
        int n = 4;
        int nthTerm = nthTerm(a, d, n);
        System.out.println(n + "th term of the arithmetic sequence with first term "+a+" and common difference "+d+" is "+nthTerm);
    } 
}
