public class ExceptionHandlingExample {
	public static void main(String[] args) {
		try {
			int num1 = 30;
			int num2 = 0;
			int result = num1/num2;
			System.out.println(result);
		} 

		catch (ArithmeticException e) {
			System.out.println("Division cannot be done by 0");
			e.printStackTrace();
		}
	}
}
