def avg_list(nums):
   total = 0
   for n in nums:
      total += n
   return total / len(nums)

print(avg_list([4, 3, 6, 10, 8]))

# Output
6.4

def is_prime(number):
   if number <= 1:
      return False
   else:
      for n in range(2, number):
         if number % n == 0:
            return False
   return True

print(is_prime(7))

# Output
True
