#include<stdio.h>

int main()
{
   int i, n1 = 0, n2 = 1, n3;

   printf("The first 10 terms of the Fibonacci sequence are:\n");
   printf("%d\n%d\n", n1, n2);

   for (i = 3; i <= 10; ++i) 
   {
      n3 = n1 + n2;
      printf("%d\n", n3);
      n1 = n2;
      n2 = n3;
   }
   return 0;
