# Function to validate a given IPv4 address. 
def validate_IPv4(s): 
    l = s.split(".") 
    if len(l) != 4: 
        return False
    for x in l: 
        if x != '*' and x != '#' and (not x.isdigit() or int(x)>255): 
            return False
    return True

# Driver Code 
s = "192.168.1.*"
if validate_IPv4(s): 
    print("valid") 
